mapper.select:
select RN.* from (select RN1.* from (select * from board
id like concat('%',#{keyword},'%') or title like concat('%',#{keyword},'%') or contents like concat('%',#{keyword},'%')
id like concat('%',#{keyword},'%')
title like concat('%',#{keyword},'%')
contents like concat('%',#{keyword},'%')
)RN1 where menu="EVENT" order by writeDay desc)RN limit #{offset}, #{limit}

select RN.* from (select RN1.* from (select * from board
id like concat('%',#{keyword},'%') or title like concat('%',#{keyword},'%') or contents like concat('%',#{keyword},'%')
id like concat('%',#{keyword},'%')
title like concat('%',#{keyword},'%')
contents like concat('%',#{keyword},'%')
)RN1 where category = "EVENT_IT_EVENT" order by writeDay desc)RN limit #{offset}, #{limit}

select RN.* from (select RN1.* from (select * from board
id like concat('%',#{keyword},'%') or title like concat('%',#{keyword},'%') or contents like concat('%',#{keyword},'%')
id like concat('%',#{keyword},'%')
title like concat('%',#{keyword},'%')
contents like concat('%',#{keyword},'%')
)RN1 where category = "EVENT_MARKETING" order by writeDay desc)RN limit #{offset}, #{limit}

mapper.update:
update board
set category = #{category}, title = #{title}, contents = #{contents}
where bno =	#{bno} and menu = 'EVENT'
