mapper.select:
select RN.* from (select RN1.* from (select * from board
where id like concat('%',#{keyword},'%')
where title like concat('%',#{keyword},'%')
where contents like concat('%',#{keyword},'%')
where title like concat('%',#{keyword},'%') or contents like concat('%',#{keyword},'%')
) RN1 where menu="NOTICE" order by writeDay desc) RN limit 0,5;
